import { useId } from 'react'
import clsx from 'clsx'

export function Logomark({
  invert = false,
  filled = false,
  ...props
}: React.ComponentPropsWithoutRef<'svg'> & {
  invert?: boolean
  filled?: boolean
}) {
  let id = useId()

  return (
    <svg viewBox="0 0 32 32" aria-hidden="true" {...props}>
      <rect
        clipPath={`url(#${id}-clip)`}
        className={clsx(
          'h-8 transition-all duration-300',
          invert ? 'fill-white' : 'fill-neutral-950',
          filled ? 'w-8' : 'w-0 group-hover/logo:w-8',
        )}
      />
      <use
        href={`#${id}-path`}
        className={invert ? 'stroke-white' : 'stroke-neutral-950'}
        fill="none"
        strokeWidth="1.5"
      />
      <defs>
        <path
          id={`${id}-path`}
          d="M3 9.5C3 8.672 3.672 8 4.5 8H12.5C13.328 8 14 8.672 14 9.5V14.5C14 15.328 13.328 16 12.5 16H4.5C4.10218 16 3.72064 15.842 3.43934 15.5607C3.15804 15.2794 3 14.8978 3 14.5V9.5ZM19 11.5C19 10.672 19.672 10 20.5 10H27.5C28.328 10 29 10.672 29 11.5V22.5C29 23.328 28.328 24 27.5 24H20.5C20.1022 24 19.7206 23.842 19.4393 23.5607C19.158 23.2794 19 22.8978 19 22.5V11.5ZM5 21.5C5 20.672 5.672 20 6.5 20H13.5C14.328 20 15 20.672 15 21.5V24.5C15 25.328 14.328 26 13.5 26H6.5C6.10218 26 5.72064 25.842 5.43934 25.5607C5.15804 25.2794 5 24.8978 5 24.5V21.5Z"
        />
        <clipPath id={`${id}-clip`}>
          <use href={`#${id}-path`} />
        </clipPath>
      </defs>
    </svg>
  )
}

